import React, { Component } from 'react';
import Square from './components/Square'
import './App.css';

export default class App extends Component {
  constructor(props) {
    super(props)
      this.state= {
        squares: [...Array(9).fill("?")],
        jellyBeanLoc: Math.floor(Math.random() * 9)
      }
  }

  render() {
    let { squares } = this.state
    let grid = squares.map((value, index) => {
      return (
        <Square
          square={ value }
          index={ index }
        />
      )
    })
    return(
      <>
        <div id="board">
          <div id="column1">
            <h1>The Jelly Bean Crisis</h1>
            <p>Help Miss Greta find her jelly beans! They fell out of the window all over the yard and she is too distraught to search herself. Find all the jelly beans before Miss Greta dies.</p>
            <p>
            - 🐷 Miss Greta
            </p>
          </div>
          <div id="column2">
            <div id="grid">
              { grid }
            </div>
          </div>
        </div>
      </>
    )
  }
}
