import React, { Component } from 'react';
import Square from './Square'



export default class Board extends Component {
  render() {
    let { squares } = this.props
    let grid = squares.map((value, index) => <Square square={ value } key={ index }/>)
    return(
      <>
        <h3>BOARD</h3>
        <div id="gameBoard">
        { grid }
        </div>
      </>
    )
  }
}
