import React, { Component } from 'react';
import Square from './components/Square'
import './App.css';

export default class App extends Component {
  constructor(props) {
    super(props)
      this.state= {
        squares: [...Array(9).fill("?")],
        jellyBeanLoc: null,
        snailLoc: null
      }
  }

  startGame = () => {
    let bean = Math.floor(Math.random() * 9)
    let snail = Math.floor(Math.random() * 9)
    if(bean === snail) {
      snail = Math.floor(Math.random() * 9)
      bean = Math.floor(Math.random() * 9)
    }
    this.setState({
      squares: [...Array(9).fill("?")],
      jellyBeanLoc: bean,
      snailLoc: snail
    })
    console.log("bean", bean);
  }

  gameLogic = (loc) => {
    let { squares, jellyBeanLoc, snailLoc } = this.state
    console.log(loc);
    if(jellyBeanLoc !== null && snailLoc !== null) {
      this.setState({ squares: squares.map((value, index) => {
        if(loc === jellyBeanLoc && loc === index) {
          return "bean"
        } else if(loc === snailLoc && loc === index) {
          return "snail"
        } else {
          return value
        }
      })})
    }
    if(loc === jellyBeanLoc) {
      alert("winner!")
      this.startGame()
    }
  }

  render() {
    let { squares } = this.state
    let grid = squares.map((value, index) => {
      return (
        <Square
          value={ value }
          index={ index }
          key={ index }
          gameLogic={ this.gameLogic }
        />
      )
    })
    return(
      <>
        <div id="board">
          <div id="column1">
            <h1>The Jelly Bean Crisis</h1>
            <p>Help Miss Greta find her jelly beans! They fell out of the window all over the yard and she is too distraught to search herself. Find all the jelly beans before Miss Greta dies. Oh and keep an eye out for snails!</p>
            <p>
            - Miss Greta
            </p>
            <button onClick={ this.startGame }>
              Start Game
            </button>
          </div>
          <div id="column2">
            <div id="grid">
              { grid }
            </div>
          </div>
        </div>
      </>
    )
  }
}
